function w3_open(){document.getElementById("mySidebar").style.display="block",document.getElementById("myOverlay").style.display="block"}function w3_close(){document.getElementById("mySidebar").style.display="none",document.getElementById("myOverlay").style.display="none"}function onClick(e){document.getElementById("img01").src=e.src,document.getElementById("modal01").style.display="block",document.getElementById("caption").innerHTML=e.alt}